Questionnaire](\r\n\t[questionId] [int] IDENTITY(1,1) NOT NULL,\r\n\t[questionText] [nvarchar](255) NULL,\r\n\t[answer] [nvarchar](255) NULL,\r\n\t[employeeId] [int] NULL,\r\n CONSTRAINT [PK_Questionnaire] PRIMARY KEY CLUSTERED \r\n(\r\n\t[questionId] ASC\r\n)WITH (PAD_INDEX = OFF, STATISTICS_NORECOMPUTE = OFF, IGNORE_DUP_KEY = OFF, ALLOW_ROW_LOCKS = ON, ALLOW_PAGE_LOCKS = ON) ON [PRIMARY]\r\n) ON [PRIMARY]\r\nGO\r\n"],"metadata":{"urn":"Server[@Name='LAPTOP-8MENBN35\\SQLEXP2017']/Database[@Name='Mohammed11254']/Table[@Name='Questionnaire' and @Schema='dbo']","object_type":"Table"}},{"cell_type":"markdown","source":["# [dbo].[vw_FullEmployeeReport]"],"metadata":{"urn":"Server[@Name='LAPTOP-8MENBN35\\SQLEXP2017']/Database[@Name='Mohammed11254']/View[@Name='vw_FullEmployeeReport' and @Schema='dbo']","object_type":"View"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  View [dbo].[vw_FullEmployeeReport]    Script Date: 2/17/2026 5:30:32 PM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","CREATE VIEW [dbo].[vw_FullEmployeeReport] AS\r\nSELECT \r\n    e.employeeId,\r\n    e.fName AS FirstName,\r\n    e.lName AS LastName,\r\n    e.jopTitle,\r\n    e.hireDate,\r\n    e.email,\r\n    e.isActive,\r\n\r\n    MAX(a.attendanceDate) AS LastAttendance,\r\n    MAX(CASE WHEN a.status = 1 THEN 'Present' WHEN a.status = 0 THEN 'Absent' ELSE NULL END) AS AttendanceStatus,\r\n\r\n    SUM(p.points) AS TotalPoints,\r\n\r\n    MAX(ev.evaluationDate) AS LastEvaluationDate,\r\n    MAX(ev.totalPoint) AS LastTotalPoint,\r\n\r\n    -- Feedback\r\n    STUFF((\r\n        SELECT ' | ' + q.questionText + ': ' + q.answer\r\n        FROM Questionnaire q\r\n        WHERE q.employeeId = e.employeeId\r\n        FOR XML PATH(''), TYPE).value('.', 'NVARCHAR(MAX)'), 1, 3, '') AS Feedback,\r\n\r\n    -- Skills\r\n    STUFF((\r\n        SELECT ', ' + s.name\r\n        FROM EmployeeSkills es\r\n        JOIN Skills s ON es.skillId = s.skillId\r\n        WHERE es.employeeId = e.employeeId\r\n        FOR XML PATH(''), TYPE).value('.', 'NVARCHAR(MAX)'), 1, 2, '') AS Skills,\r\n\r\n    MAX(ua.username) AS Username,\r\n    MAX(ua.role) AS Role\r\n\r\nFROM employee e\r\nLEFT JOIN Attendance a ON e.employeeId = a.employeeId\r\nLEFT JOIN Points p ON e.employeeId = p.employeeId\r\nLEFT JOIN Evaluation ev ON e.employeeId = ev.employeeId\r\nLEFT JOIN UsersAdmins ua ON e.employeeId = ua.employeeId\r\n\r\nGROUP BY \r\n    e.employeeId, e.fName, e.lName, e.jopTitle, e.hireDate, e.email, e.isActive;\r\n\r\nGO\r\n"],"metadata":{"urn":"Server[@Name='LAPTOP-8MENBN35\\SQLEXP2017']/Database[@Name='Mohammed11254']/View[@Name='vw_FullEmployeeReport' and @Schema='dbo']","object_type":"View"}},{"cell_type":"markdown","source":["# [dbo].[vw_EmployeeAttendance]"],"metadata":{"urn":"Server[@Name='LAPTOP-8MENBN35\\SQLEXP2017']/Database[@Name='Mohammed11254']/View[@Name='vw_EmployeeAttendance' and @Schema='dbo']","object_type":"View"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  View [dbo].[vw_EmployeeAttendance]    Script Date: 2/17/2026 5:30:32 PM ******/\r\nSET ANSI_NULLS ON\r\n","GO\r\n","SET QUOTED_IDENTIFIER ON\r\n","GO\r\n","CREATE VIEW [dbo].[vw_EmployeeAttendance] AS\r\nSELECT \r\n    e.employeeId,\r\n    e.fName,\r\n    e.lName,\r\n    a.attendanceDate,\r\n    a.status\r\nFROM employee e\r\nINNER JOIN Attendance a ON e.employeeId = a.employeeId;\r\nGO\r\n"],"metadata":{"urn":"Server[@Name='LAPTOP-8MENBN35\\SQLEXP2017']/Database[@Name='Mohammed11254']/View[@Name='vw_EmployeeAttendance' and @Schema='dbo']","object_type":"View"}},{"cell_type":"markdown","source":["# [dbo].[vw_EmployeePoints]"],"metadata":{"urn":"Server[@Name='LAPTOP-8MENBN35\\SQLEXP2017']/Database[@Name='Mohammed11254']/View[@Name='vw_EmployeePoints' and @Schema='dbo']","object_type":"View"}},{"outputs":[],"execution_count":0,"cell_type":"code","source":["/****** Object:  View [dbo].[vw_EmployeePoints]    Script Date: 2/